import React, { useState } from "react";
import NavbarComponent from "../components/Navbar";
import HeaderComponent from "../components/header";
import DetailComponent from "../components/detail";
import IconComponent from "../components/icon";
import AboutComponent from "../components/about";
import FooterComponent from "../components/footer";
import TimePicker from "../components/Timepicker";
import MenuItems from "../components/MenuItems";

const DineOut = () => {

    const [orderName, setOrderName] = useState("");
    const [orderSeating, setOrderSeating] = useState("");
    const [orderGuest, setOrderGuest] = useState("")
    const [cart, setCart] = useState([]);

    const renderCart = () => {
        setCart([...cart, <MenuItems />]);
    }

    return (
        <div>
            <NavbarComponent />

            <div>
                <form>
                    <h5>Name</h5>
                    <input
                        placeholder="Name"
                        onChange={(e) => setOrderName(e.target.value)}
                    />
                    <TimePicker />
                    <h5>Order Items</h5>
                    <div>
                        {cart.map((item) =>
                        (
                            <div className="col s12 m7">
                                <h5 className="header itemName" name="name">{item.name}</h5>
                                <div className="card horizontal">
                                    <div className="card-image">
                                        <img className="card-image" src={item.image} alt="menuItem" />
                                    </div>
                                    <div className="card-stacked">
                                        <div className="card-content">
                                            <p>I am a very simple card. I am good at containing small bits of information.</p>
                                        </div>
                                    </div>
                                    <div className="card-action price" name="price">
                                        <h6>{item.price}</h6>
                                    </div>
                                    <div className="card-action">
                                        <button className="btn waves-effect waves-light add-to-cart" type="submit" name="action">Add to Cart</button>
                                    </div>
                                </div>
                            </div>
                        ))} 
                        </div>
                        
                        Order items go here
                            
                    <h5>Total</h5>
                    <input
                        placeholder="Price total goes here" type="number"
                        onChange={(e) => setOrderGuest(e.target.value)}
                    />
                    <button className="btn waves-effect waves-light" type="submit" name="action"> Place Order</button>
                </form>
            </div>
            <FooterComponent />

        </div>
    );

}

export default DineOut;
