import React, {useState} from "react";
import NavbarComponent from "../components/Navbar";
import HeaderComponent from "../components/header";
import DetailComponent from "../components/detail";
import IconComponent from "../components/icon";
import AboutComponent from "../components/about";
import FooterComponent from "../components/footer";
import MenuItems from "../components/MenuItems";

const Menu = () => {
    const [orderCardValues, setOrderCardValues] = useState({name: 'inital values', value: 'inital value'})

    let addBtn = document.getElementsByClassName("add-to-cart");
    let itemName = document.getElementsByClassName("itemName");
    let itemPrice = document.getElementsByClassName("itemPrice");
    let receipt = document.getElementById("receipt");


    for(let i = 0; i < addBtn.length; i++) {
      let newRow = document.createElement("tr");
      let newOrderName = document.createElement("td");
      let newOrderPrice = document.createElement("td");
      let orderName = itemName[i]
      let orderPrice = itemPrice[i]

      addBtn[i].addEventListener("click", () => {
          newOrderName.textContent = orderName
          newOrderPrice.textContent = orderPrice
          receipt.appendChild(newRow)
          newRow.appendChild(orderName, orderPrice)
      })
  
  }
  
return(
    <div>
        <NavbarComponent/>
        <div class="row">
          <div class="col s12 m5">
            <div class="card-panel ">
              <table id="receipt">
                  <tr id="receipt-header">
                      <th>Item</th>
                      <th></th>
                      <th></th>
                      <th>Price</th>
                  </tr>
                  <tr id="menu-space">
                      {orderCardValues.name}
                      {orderCardValues.value}
                  </tr>
                  <tr id="receipt-footer">
                      <th>Total</th>
                      <th></th>
                      <th></th>
                      <th>Price:</th>
                  </tr>
                  <div>
                    <button className="btn waves-effect waves-light" id="place-order" type="submit" name="action">Place Order</button>
                  </div>
              </table>
            </div>
          </div>
        </div>

        <div>
          {menueItems.map(item => {
            return <ItemComponent item={item} setOrderCardValues={setOrderCardValues}/>
          })}

{/* lets say this code is in the item component:
onclik, we will create an object {name: 'the item', value: the value}.. both are available in this.props
setOrderCardValues({}).. which is also available in props */}
        <h2>Our Menu</h2>
          <MenuItems/>


      </div>

        
        <FooterComponent/>
    </div>
    );
    
}

export default Menu;
