import React, {useState} from "react";
import NavbarComponent from "../components/Navbar";
import FooterComponent from "../components/footer";
import MenuItems from "../components/MenuItems";

const Menu = () => {

  const [items] = useState([
    {
      name: "Menu Item #1",
      price: "$" + 10.99,
      image: require("../images/Lamb.jpg")
    },
    {
      name: "Menu Item #2",
      price: "$" + 10.99,
      image: require("../images/crab.jpg")
    },
    {
      name: "Menu Item #3",
      price: "$" + 10.99,
      image: require("../images/filletMinion.jpg")
    },
    {
      name: "Menu Item #4",
      price: "$" + 10.99,
      image: require("../images/Pho.jpg")
    },
    {
      name: "Menu Item #5",
      price: "$" + 10.99,
      image: require("../images/food.jpg")
    },
    {
      name: "Menu Item #6",
      price: "$" + 10.99,
      image: require("../images/shrimp.jpg")
    }
  ]);



return(
    <div>
        <NavbarComponent/>
        <div className="row">
          <div className="col s12 m5">
            <div className="card-panel ">
                  <h5>Selected Items</h5>
                  <div>Order Items go here</div>
                  <div>
                    <button className="btn waves-effect waves-light" id="place-order" type="submit" name="action">Place Order</button>
                  </div>

            </div>
          </div>
        </div>

        <div>
          {/* {menueItems.map(item => {
            return <ItemComponent item={item} setOrderCardValues={setOrderCardValues}/>
          })} */}

{/* lets say this code is in the item component:
onclik, we will create an object {name: 'the item', value: the value}.. both are available in this.props
setOrderCardValues({}).. which is also available in props */}

        <h2>Our Menu</h2>
        <div>
        {items.map((item) => 
        (
        <div className="col s12 m7">
          <h5 className="header itemName" name="name">{item.name}</h5>
          <div className="card">
            <div className="card-image">
              <img className="menuItem" src={item.image} alt="menuItem" />
            </div>
            <div className="card-stacked">
              <div className="card-content">
                <p>I am a very simple card. I am good at containing small bits of information.</p>
              </div>
            </div>
            <div className="card-action price" name="price">
              <h6>{item.price}</h6>
            </div>
            <div className="card-action">
              <button className="btn waves-effect waves-light add-to-cart" type="submit" name="action" >Add to Cart</button>
            </div>
          </div>
        </div>
        )
)}
      </div>


      </div>


        <FooterComponent/>
    </div>
    );

}

export default Menu;
