import React,  { useState, useEffect } from 'react';
import PrivateRoute from './PrivateRoute';
import Axios from "axios";



//React Router Imports
import {
  BrowserRouter as Router,
  Route,
  Switch
} from "react-router-dom"

//Pages
import Login from "./pages/login";
import Landing from "./pages/landing";
import Reservation from "./pages/reservation";
import DineOut from "./pages/dineOut";
import Register from "./pages/register";
import Menu from "./pages/menu";



function App() {

  const [loginEmail, setLoginEmail] = useState("");
  const [loginPassword, setLoginPassword] = useState("");
  const [authenticated, setAuthenticated] = useState(false);


 useEffect(()=>{
   if(sessionStorage.getItem("loggedIn")===true){
     setAuthenticated(true)
   }
 },[authenticated])

   const loginUser = (e) => {

    e.preventDefault();

    Axios({
      method: "POST",
      data: {
        username: loginEmail,
        password: loginPassword,
      },
      withCredentials: true,
      url: "api/login",
    }).then((res) => console.log(res));
   };


   function handleInputChange(e){
let name = e.target.name;
let value = e.target.value

switch (name){
    case "user":
    setLoginEmail(value)
    break;
    case"password":
    setLoginPassword(value)
    break;
}


   }


  return (
    <div className="App">
      <Router>
        <Switch>
          <Route exact path="/">
          <Login handleInputChange={handleInputChange} auth={authenticated} loginUser={loginUser} />
          </Route>
          <Route exact path="/register" component={Register}/>
          <PrivateRoute exact path="/user" component={Landing}/>
          <PrivateRoute exact path="/menu" component={Menu}/>
          <PrivateRoute exact path="/reservation" component={Reservation}/>
          <PrivateRoute exact path="/dineOut" component={DineOut}/>
        </Switch>
      </Router>
    </div>
  );
}
export default App;
