import React, { useState } from "react";
import NavbarComponent from "../components/Navbar";
import HeaderComponent from "../components/header";
import DetailComponent from "../components/detail";
import IconComponent from "../components/icon";
import AboutComponent from "../components/about";
import FooterComponent from "../components/footer";
import TimePicker from "../components/Timepicker";
import MenuItems from "../components/MenuItems";
import Axios from "axios";
import Datepicker from "../components/Datepicker";

const DineOut = () => {

    const [orderName, setOrderName] = useState("");
    const [orderDate, setOrderDate] = useState("");
    const [orderTime, setOrderTime] = useState("")
    const [orderItem, setOrderItem] = useState("")
    const [orderTotal, setOrderTotal] = useState("");


    const placeOrder = (e) => {
        e.preventDefault()

        let data = {
            Name: orderName,
            Date: orderDate
        }

        Axios.post("/api/login", data)
            .then((res) => {
                console.log(res)
                if (res.status === 200) {
                    sessionStorage.setItem("loggedIn", true);
                    sessionStorage.setItem("id", res.data.id);
                    setAuthenticated(true);
                }
            });
    };


    return (
        <div>
            <NavbarComponent />

            <div>
                <form>
                    <h5>Name</h5>
                    <input
                        placeholder="Name"
                        onChange={(e) => setOrderName(e.target.value)}
                    />
                    <div>
                        <h5>Date</h5>
                        <div className="cell"><span className="label primary"></span>
                            <input id="orderName" placeholder="yyyy-mm-dd" type="Date" onChange={(e) => setOrderDate(e.target.value)} />
                        </div>

                    </div>
                    <div>
                        <h5>Time</h5>
                        <div className="cell"><span className="label primary"></span>
                            <input id="orderTime" placeholder="Select Time" type="time" onChange={(e) => setOrderTime(e.target.value)} />
                        </div>

                    </div>
                    <h5>Order Items</h5>
                    {/* <div>
                        {cart.map((item) =>
                        (
                            <div className="col s12 m7">
                                <h5 className="header itemName" name="name">{item.name}</h5>
                                <div className="card horizontal">
                                    <div className="card-image">
                                        <img className="card-image" src={item.image} alt="menuItem" />
                                    </div>
                                    <div className="card-stacked">
                                        <div className="card-content">
                                            <p>I am a very simple card. I am good at containing small bits of information.</p>
                                        </div>
                                    </div>
                                    <div className="card-action price" name="price">
                                        <h6>{item.price}</h6>
                                    </div>
                                    <div className="card-action">
                                        <button className="btn waves-effect waves-light add-to-cart" type="submit" name="action">Add to Cart</button>
                                    </div>
                                </div>
                            </div>
                        ))} 
                        </div> */}

                        Order items go here

                    <h5>Total</h5>
                    <input
                        placeholder="Price total goes here" type="number"
                        onChange={(e) => setOrderTotal(e.target.value)}
                    />
                    <button className="btn waves-effect waves-light" type="submit" name="action"> Place Order</button>
                </form>
            </div>
            <FooterComponent />

        </div>
    );

}

export default DineOut;
