import React, { useState } from "react";
import NavbarComponent from "../components/Navbar";
import FooterComponent from "../components/footer";
import {Link} from "react-router-dom";
import Axios from "axios";


const DineOut = () => {

    const [orderName, setOrderName] = useState("");
    const [orderDate, setOrderDate] = useState("");
    const [orderTime, setOrderTime] = useState("")
    const [orderItem, setOrderItem] = useState("")
    const [orderTotal, setOrderTotal] = useState("");


    const placeOrder = (e) => {
        e.preventDefault();
        Axios({
            method: "POST",
            url: "/api/dineOut",
            data: {
              Name: orderName,
              Date: orderDate,
              Time: orderTime,
              Order: orderItem,
              Total: orderTotal
            },
            withCredentials: true,
          }).then((res) => console.log(res));
        };

        const clearCart = () => {
            setCart([]);
          };

          const setQuantity = (product, amount) => {
            const newCart = [...cart];
            newCart.find(
              (item) => item.name === product.name
            ).quantity = amount;
            setCart(newCart);
          };

          const removeFromCart = (productToRemove) => {
            setCart(
              cart.filter((product) => product !== productToRemove)
            );
          };


    return (
        <div>
            <NavbarComponent />

            <div>
                <form>
                    <h5>Name</h5>
                    <input
                        placeholder="Name"
                        onChange={(e) => setOrderName(e.target.value)}
                    />
                    <div>
                        <h5>Date</h5>
                        <div className="cell"><span className="label primary"></span>
                            <input id="orderName" placeholder="yyyy-mm-dd" type="Date" onChange={(e) => setOrderDate(e.target.value)} />
                        </div>

                    </div>
                    <div>
                        <h5>Time</h5>
                        <div className="cell"><span className="label primary"></span>
                            <input id="orderTime" placeholder="Select Time" type="time" onChange={(e) => setOrderTime(e.target.value)} />
                        </div>

                    </div>
                    <h5>Order Items</h5>
                    <p><Link to="/menu">See Menu</Link></p>
                    {cart.length > 0 && (
        <button onClick={clearCart}>Clear Cart</button>
      )}
      <div className="products">
        {cart.map((product, idx) => (
          <div className="product" key={idx}>
            <h3>{product.name}</h3>
            <h4>${product.cost}</h4>
            <input
              value={product.quantity}
              onChange={(e) =>
                setQuantity(
                  product,
                  parseInt(e.target.value)
                )
              }
            />
            <img src={product.image} alt={product.name} />
            <button onClick={() => removeFromCart(product)}>
              Remove
            </button>
          </div>
        ))}
      </div>

                    <h5>Total</h5>
                    <input
                        placeholder="Price total goes here" type="number"
                        onChange={(e) => setOrderTotal(e.target.value)}
                    />
                    <button className="btn waves-effect waves-light" type="submit" name="action" onClick={placeOrder}> Place Order</button>
                </form>
            </div>
            <FooterComponent />

        </div>
    );

}

export default DineOut;
